import React from 'react';

const TrafficMonitoring = () => {
  // Function to show/hide charts and tables dynamically
  const showChart = (chartId) => {
    document.getElementById('networkTrafficChart').style.display = 'none';
    document.getElementById('suspiciousTrafficTable').style.display = 'none';
    document.getElementById('networkUsageChart').style.display = 'none';
    document.getElementById(chartId).style.display = 'block';
  };

  const showTable = (tableId) => {
    document.getElementById('networkTrafficChart').style.display = 'none';
    document.getElementById('suspiciousTrafficTable').style.display = 'none';
    document.getElementById('networkUsageChart').style.display = 'none';
    document.getElementById(tableId).style.display = 'block';
  };

  return (
    <div className="card">
      <div className="card-body">
        <div className="d-flex gap-1 mb-3" role="group" aria-label="Traffic Monitoring Options">
            <h5 className="">Network Trafficking: </h5>
            <button type="button" className="btn btn-outline-primary" onClick={() => showChart('networkTrafficChart')}>Network Traffic</button>
            <button type="button" className="btn btn-outline-primary" onClick={() => showTable('suspiciousTrafficTable')}>Suspicious Traffic</button>
            <button type="button" className="btn btn-outline-primary" onClick={() => showChart('networkUsageChart')}>Network Usage</button>
        </div>
        <div id="chartContainer">
          <canvas id="networkTrafficChart" width="400" height="200"></canvas>
        </div>
      </div>
    </div>
  );
};

export default TrafficMonitoring;
