// App.js
import React, { useState, useEffect }  from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import './styles/styles.css'; // Import your custom CSS
import Navbar from './components/Navbar';
import SecurityCard from './components/SecurityCard';
import DeviceList from './components/DeviceList';
import TrafficMonitoring from './components/TrafficMonitoring';
import DevicesAtRisk from './components/riskDevices';
import ConsoleOutput from './components/console';
import axios from "axios";

function App() {
  const [message, setMessage] = useState("");
  const [responseData, setResponseData] = useState(null);

  // Fetch data from Flask API on load
  useEffect(() => {
    axios
      .get("http://127.0.0.1:5000/api/data")
      .then((response) => {
        setMessage(response.data.message);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  // Send data to Flask API
  const sendData = () => {
    const dataToSend = { name: "John Doe", age: 25 };
    axios
      .post("http://127.0.0.1:5000/api/data", dataToSend)
      .then((response) => {
        setResponseData(response.data);
        console.log("Response from Flask:", response.data);
      })
      .catch((error) => {
        console.error("Error sending data:", error);
      });
  };
  return (
    <div>
      <Navbar />

      <div className="container mt-4">
        <div className="row">
          <div className="col-md-4">
            <h3 className="mb-4">üõ°Ô∏è Network & Device Security</h3>
            <SecurityCard />
            <DevicesAtRisk />
            <h3 className='mt-4 mb-3'>üñ≥ System Console</h3>
            <ConsoleOutput />
          </div>
          <div className="col-md-8">
            <TrafficMonitoring />
            <DeviceList /> 
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
