import React, { useState, useEffect } from 'react';

function BandwidthUsage() {
  const [liveMonitoring, setLiveMonitoring] = useState(true);
  const [bandwidthData, setBandwidthData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!liveMonitoring) return;

    const fetchBandwidth = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/api/bandwidth');
        const data = await response.json();

        if (data.status === 'Success') {
          setBandwidthData(data.bandwidth);
        } else {
          console.error('Error fetching bandwidth data:', data.error);
        }
      } catch (error) {
        console.error('Error:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchBandwidth();

    const interval = setInterval(fetchBandwidth, 5000); // Fetch data every 5 seconds
    return () => clearInterval(interval); // Cleanup interval on component unmount
  }, [liveMonitoring]);

  return (
    <div className='mt-4'>
      <h3>ðŸ“¡ Bandwidth Usage</h3>

      <div className="card mt-4">
        <div className="card-body">
          <div className="form-check form-switch mb-3">
            <input
              className="form-check-input"
              type="checkbox"
              id="liveMonitoringToggle"
              checked={liveMonitoring}
              onChange={() => setLiveMonitoring(!liveMonitoring)}
            />
            <label className="form-check-label" htmlFor="liveMonitoringToggle">
              Live Monitoring
            </label>
          </div>
          {loading ? (
            <p>Loading bandwidth data...</p>
          ) : (
            <table className="table">
              <thead>
                <tr>
                  <th scope="col">Interface</th>
                  <th scope="col">Received Data (Bytes)</th>
                  <th scope="col">Transmitted Data (Bytes)</th>
                </tr>
              </thead>
              <tbody>
                {bandwidthData.map((data, index) => (
                  <tr key={index}>
                    <td>{data.interface}</td>
                    <td>{data.receive_bytes.toLocaleString()}</td>
                    <td>{data.transmit_bytes.toLocaleString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
}

export default BandwidthUsage;
