import React, { useState, useEffect } from 'react';

const SystemData = () => {
  const [data, setData] = useState({
    cpuMemory: null,
    wirelessClients: null,
    firewallRules: null,
    uptimeLoad: null,
    networkConfig: null,
  });
  const [loading, setLoading] = useState(true);

  const fetchData = async (endpoint) => {
    try {
      const response = await fetch(`http://127.0.0.1:5000/api/${endpoint}`);
      const result = await response.json();
      if (result.status === 'Success') {
        return result;
      } else {
        console.error(`Error fetching ${endpoint}:`, result.error);
        return { error: result.error };
      }
    } catch (error) {
      console.error(`Error fetching ${endpoint}:`, error);
      return { error: error.message };
    }
  };

  useEffect(() => {
    const loadData = async () => {
      setLoading(true);
      const cpuMemory = await fetchData('cpu_memory');
      const wirelessClients = await fetchData('wireless_clients');
      const firewallRules = await fetchData('firewall_rules');
      const uptimeLoad = await fetchData('uptime_load');
      const networkConfig = await fetchData('network_config');
      setData({
        cpuMemory,
        wirelessClients,
        firewallRules,
        uptimeLoad,
        networkConfig,
      });
      setLoading(false);
    };

    loadData();
  }, []);

  if (loading) {
    return <p>Loading system data...</p>;
  }

  return (
    <div className="container">
      <h2>System Data</h2>
      <div className="mb-4">
        <h4>CPU and Memory Usage</h4>
        <pre style={{ backgroundColor: '#000', color: '#0f0', padding: '10px' }}>
          {data.cpuMemory.cpu_usage || 'N/A'}
          {data.cpuMemory.memory_usage || 'N/A'}
        </pre>
      </div>
      <div className="mb-4">
        <h4>Wireless Clients</h4>
        <pre style={{ backgroundColor: '#000', color: '#0f0', padding: '10px' }}>
          {data.wirelessClients.wireless_clients || 'N/A'}
        </pre>
      </div>
      <div className="mb-4">
        <h4>Firewall Rules</h4>
        <pre style={{ backgroundColor: '#000', color: '#0f0', padding: '10px' }}>
          {data.firewallRules.firewall_rules || 'N/A'}
        </pre>
      </div>
      <div className="mb-4">
        <h4>Uptime and Load</h4>
        <pre style={{ backgroundColor: '#000', color: '#0f0', padding: '10px' }}>
          {data.uptimeLoad.uptime_load || 'N/A'}
        </pre>
      </div>
      <div className="mb-4">
        <h4>Network Configuration</h4>
        <pre style={{ backgroundColor: '#000', color: '#0f0', padding: '10px' }}>
          {data.networkConfig.network_config || 'N/A'}
        </pre>
      </div>
    </div>
  );
};

export default SystemData;
