import React, { useState } from "react";

function FetchDataButton() {
    const [fetchedData, setFetchedData] = useState(null);

    const fetchData = async () => {
        try {
            const response = await fetch("http://127.0.0.1:5000/api/data");
            const data = await response.json();
            console.log("Received from Flask:", data);
            setFetchedData(data); // Update state with fetched data
        } catch (error) {
            console.error("Error fetching data:", error);
        }
    };

    return (
        <div>
            <h1>Fetch Data from Flask</h1>
            <button onClick={fetchData}>Fetch Data</button>
            <div>
                <h2>API Response:</h2>
                <pre>{fetchedData ? JSON.stringify(fetchedData, null, 2) : "No data yet"}</pre>
            </div>
        </div>
    );
}

export default FetchDataButton;
