import React from 'react';

const devices = [
  { name: 'Smart Light 1', status: 'Online', security: 'Secure' },
  { name: 'Smart Light 2', status: 'Online', security: 'Secure' },
  { name: 'HP Printer', status: 'Online', security: 'Moderate Risk' },
  // Add other devices as needed
];

const DeviceList = () => (
  <div>
    <table className="table table-striped">
      <thead>
        <tr>
          <th>Device Name</th>
          <th>Status</th>
          <th>Security Status</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {devices.map((device, index) => (
          <tr key={index}>
            <td>{device.name}</td>
            <td>{device.status}</td>
            <td><span className={`badge bg-${device.security === 'Secure' ? 'success' : 'warning'}`}>{device.security}</span></td>
            <td><button className="btn btn-sm btn-primary">Settings</button></td>
          </tr>
        ))}
      </tbody>
    </table>
  </div>
);

export default DeviceList;
