import React, { useState } from 'react';

// Sample component for displaying device risk levels
function DevicesAtRisk() {
  const [liveMonitoring, setLiveMonitoring] = useState(true);

  // Sample data for devices
  const devices = [
    { name: 'Smart Thermostat', risk: 'Moderate Risk', riskClass: 'badge bg-warning text-dark' },
    { name: 'Security Camera', risk: 'High Risk', riskClass: 'badge bg-danger' },
  ];

  return (
    <div className='mt-4'>
      <h2>Devices At Risk</h2>

      <div className="card mt-4">
        <div className="card-body">
            <div className="form-check form-switch mb-3">
                <input
                className="form-check-input"
                type="checkbox"
                id="liveMonitoringToggle"
                checked={liveMonitoring}
                onChange={() => setLiveMonitoring(!liveMonitoring)}
                />
                <label className="form-check-label" htmlFor="liveMonitoringToggle">
                Live Monitoring
                </label>
            </div>
          <table className="table">
            <thead>
              <tr>
                <th scope="col">Device Name</th>
                <th scope="col">Risk Level</th>
              </tr>
            </thead>
            <tbody>
              {devices.map((device, index) => (
                <tr key={index}>
                  <td>{device.name}</td>
                  <td>
                    <span className={device.riskClass}>{device.risk}</span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default DevicesAtRisk;
